//Moves forward x seconds
void moveForward (int x) {
 	 	motor[Left] = 30;
 		motor[Right] = 30;
 		wait1Msec(x * 1000);
}

//Turns right approx x degrees
//Did some tests and x*9 gave us the approximate correct angle
void turnRight(int x) {
 	 	motor[Left] = 20;
 		motor[Right] = -20;
 		wait1Msec(x*9);
}

//turns left approx x degrees
void turnLeft(int x) {
 	 	motor[Left] = -20;
 		motor[Right] = 20;
 		wait1Msec(x*9);
}

